var content;
var fs=require('fs');
fs.readFile('./words.txt', function read(err, data) {
    if (err) {
        throw err;
    }
    var str=data.toString();
    str=str.replace(/\n/g,' ');
    var a=str.split(' ');
    content = data;
    var nouns={};
    for(var i=2;i< a.length;i+=4){
        //console.log(a[i]);
        if(a[i+1]==='noun'){
            if(!nouns[a[i].length])
                nouns[a[i].length]=[];
            nouns[a[i].length].push(a[i]);

        }
    }
    fs.writeFile('./nouns.json', JSON.stringify(nouns), function (err) {
        if (err) return console.log(err);
        //console.log('Hello World > helloworld.txt');
    });
});
