var fC = require('MyModules/folderCreator');
var root=__dirname;
//console.log(root);
function debug(text,name){
    console.log(arguments);
    var path=root+'/'+name+'.debug';
    if(!fC.hvFolder(path)){
        fC.crFile(path,text);
    }else{
        fC.add(path,text,function(){});
    }
}
exports.debug=debug;
