var fs=require("fs");
var path=require("path");
var change=require("./pageChanger").change;
//var config=require("config/config.js");
var ROOT=__dirname+ '\\'  +  "Pages";

function sendFileSave(filePath,res,chan) {

    try{
        filePath=decodeURIComponent(filePath);
    }
    catch(e)
    {
        res.statusCode=400;
        res.end("Bad Request");
        return
    }

    if(~filePath.indexOf('\0'))
    {
        res.statusCode=400;
        res.end("Bad Request");
        return
    }

    filePath=path.normalize(path.join(ROOT,filePath));
   // console.log(filePath);
    //console.log(ROOT);
    if(filePath.indexOf(ROOT)!=0)
    {
        //console.log(228+filePath);
        res.statusCode=400;
        res.end("File not found");
        return;
    }

    fs.stat(filePath,function(err,stats)
    {
        if(err||!stats.isFile())
        {
          //  console.log(1);
            res.statusCode=400;
            res.end("File not found");
            return;
        }
        sendFile(filePath,res,chan);

    })

}
function sendFile(filePath,res,chan) {

    fs.readFile(filePath,function(err,content)
    {
        chan=chan||{};
        if(err) throw err;
        var str=content.toString();
        str=change(str,chan);
        res.end(str);
    })
}

module.exports.sendFileSave=sendFileSave;
