var config=require("config/config.json");
function login(login){
    var a=config["LP"]["login"]["maxL"];
    if(login.length>a){
        return "Логин должен быть длиной меньше "+a+" символов";
    }
    a=config["LP"]["login"]["minL"];
    if(login.length<a){
        return "Логин должен быть длиной больше "+a+" символов";
    }
    return "";
}
function pass(pass){
    var a=config["LP"]["pass"]["maxL"];
    if(pass.length>a){
        return "Пароль должен быть длиной меньше "+a+" символов";
    }
    a=config["LP"]["pass"]["minL"];
    if(pass.length<a){
        return "Пароль должен быть длиной больше "+a+" символов";
    }
    return "";
}
exports.pass=pass;
exports.login=login;